getFirstBlockEpochV2 ::
    StablePtr Ext.ConsensusRunner ->
    -- | Query type.
    Word8 ->
    -- | Query payload data.
    Ptr Word8 ->
    -- | Out pointer for the result block hash.
    Ptr Word8 ->
    IO Int64
getFirstBlockEpochV2 cptr queryType queryDataPtr outHash = do
    Ext.ConsensusRunner mvr <- deRefStablePtr cptr
    epochReq <- decodeEpochRequest queryType queryDataPtr
    response <- runMVR (Q.getFirstBlockEpoch epochReq) mvr
    case response of
        Left Q.EQEBlockNotFound -> return $ queryResultCode QRNotFound
        Left Q.EQEFutureEpoch -> return $ queryResultCode QRFutureEpoch
        Left Q.EQEInvalidEpoch -> return $ queryResultCode QRInvalidArgument
        Left Q.EQEInvalidGenesisIndex -> return $ queryResultCode QRInvalidArgument
        Right res -> do
            copyHashTo outHash (Q.BQRBlock res ())
            return $ queryResultCode QRSuccess

getWinningBakersEpochV2 ::
    StablePtr Ext.ConsensusRunner ->
    Ptr SenderChannel ->
    -- | Query type.
    Word8 ->
    -- | Query payload data.
    Ptr Word8 ->
    -- | Stream callback.
    FunPtr (Ptr SenderChannel -> Ptr Word8 -> Int64 -> IO Int32) ->
    IO Int64
getWinningBakersEpochV2 cptr channel queryType queryDataPtr cbk = do
    Ext.ConsensusRunner mvr <- deRefStablePtr cptr
    let sender = callChannelSendCallback cbk channel
    epochReq <- decodeEpochRequest queryType queryDataPtr
    response <- runMVR (Q.getWinningBakersEpoch epochReq) mvr
    case response of
        Left Q.EQEBlockNotFound -> return $ queryResultCode QRNotFound
        Left Q.EQEFutureEpoch -> return $ queryResultCode QRFutureEpoch
        Left Q.EQEInvalidEpoch -> return $ queryResultCode QRInvalidArgument
        Left Q.EQEInvalidGenesisIndex -> return $ queryResultCode QRInvalidArgument
        Right res -> do
            _ <- enqueueMessages sender res
            return $ queryResultCode QRSuccess

getBlockItemStatusV2 ::
    StablePtr Ext.ConsensusRunner ->
    -- | Transaction hash.
    Ptr Word8 ->
    Ptr ReceiverVec ->
    -- | Callback to output data.
    FunPtr CopyToVecCallback ->
    IO Int64
getBlockItemStatusV2 cptr trxHashPtr outVec copierCbk = do
    Ext.ConsensusRunner mvr <- deRefStablePtr cptr
    let copier = callCopyToVecCallback copierCbk
    trxHash <- decodeTransactionHashInput trxHashPtr
    res <- runMVR (Q.getTransactionStatus trxHash) mvr
    case res of
        Nothing -> do
            return $ queryResultCode QRNotFound
        Just ts -> case toProto ts of
            Left e -> do
                mvLog mvr Logger.External Logger.LLError $ "Internal conversion error occured for transaction '" ++ show trxHash ++ "': " ++ show e
                return $ queryResultCode QRInternalError
            Right t -> do
                let encoded = Proto.encodeMessage t
                BS.unsafeUseAsCStringLen encoded (\(ptr, len) -> copier outVec (castPtr ptr) (fromIntegral len))
                return $ queryResultCode QRSuccess

invokeInstanceV2 ::
    StablePtr Ext.ConsensusRunner ->
    -- | Block ID type.
    Word8 ->
    -- | Block ID pointer.
    Ptr Word8 ->
    -- | Contract index.
    Word64 ->
    -- | Contract subindex.
    Word64 ->
    -- | Invoker address tag.
    Word8 ->
    -- | Invoker account address pointer.
    Ptr Word8 ->
    -- | Invoker contract index.
    Word64 ->
    -- | Invoker contract subindex.
    Word64 ->
    -- | Amount.
    Word64 ->
    -- | ReceiveName pointer.
    Ptr Word8 ->
    -- | ReceiveName length.
    Word32 ->
    -- | Parameter pointer.
    Ptr Word8 ->
    -- | Parameter length.
    Word32 ->
    -- | Energy
    Word64 ->
    -- | Out pointer for writing the block hash that was used.
    Ptr Word8 ->
    Ptr ReceiverVec ->
    FunPtr CopyToVecCallback ->
    IO Int64
invokeInstanceV2 cptr blockIdType blockIdPtr contractIndex contractSubindex invokerAddressType invokerAccountAddressPtr invokerContractIndex invokerContractSubindex amount receiveNamePtr receiveNameLen parameterPtr parameterLen energy outHash outVec copierCbk = do
    Ext.ConsensusRunner mvr <- deRefStablePtr cptr
    let copier = callCopyToVecCallback copierCbk
    block <- decodeBlockHashInput blockIdType blockIdPtr
    maybeInvoker <- case invokerAddressType of
        0 -> return $ Nothing
        1 -> Just . AddressAccount <$> decodeAccountAddress invokerAccountAddressPtr
        _ -> return $ Just $ AddressContract $ ContractAddress (ContractIndex invokerContractIndex) (ContractSubindex invokerContractSubindex)
    method <- decodeReceiveName receiveNamePtr receiveNameLen
    parameter <- decodeParameter parameterPtr parameterLen
    let context =
            InvokeContract.ContractContext
                { ccInvoker = maybeInvoker,
                  ccContract = ContractAddress (ContractIndex contractIndex) (ContractSubindex contractSubindex),
                  ccAmount = Amount amount,
                  ccMethod = method,
                  ccParameter = parameter,
                  ccEnergy = Energy energy
                }
    response <- runMVR (Q.invokeContract block context) mvr
    copyHashTo outHash response
    case toProto <$> response of
        Q.BQRBlock _ (Left _) -> return $ queryResultCode QRInternalError
        Q.BQRBlock _ (Right proto) -> do
            let encoded = Proto.encodeMessage proto
            BS.unsafeUseAsCStringLen encoded (\(ptr, len) -> copier outVec (castPtr ptr) (fromIntegral len))
            return $ queryResultCode QRSuccess
        Q.BQRNoBlock -> return $ queryResultCode QRNotFound

