-- | Get the earliest time in which a baker wins the lottery.
--  Returns "unavailable" for consensus version 0.
--  For consensus version 1, it will always return a result. If the baker ID does not correspond
--  to a baker in the current reward period, the result will be the start of the following reward
--  period.
getBakerEarliestWinTimeV2 ::
    StablePtr Ext.ConsensusRunner ->
    -- | Baker ID.
    Word64 ->
    -- | Vector to write output to.
    Ptr ReceiverVec ->
    -- | Callback to output data.
    FunPtr CopyToVecCallback ->
    IO Int64
getBakerEarliestWinTimeV2 cptr baker outVec copierCbk = do
    Ext.ConsensusRunner mvr <- deRefStablePtr cptr
    let copier = callCopyToVecCallback copierCbk outVec
    runMVR (Q.getBakerEarliestWinTime (fromIntegral baker)) mvr >>= \case
        Nothing -> return $ queryResultCode QRUnavailable
        Just ts -> returnMessage copier ts

-- | Write the hash to the provided pointer, encode the message given and write it using the provided callback.
returnMessageWithBlock ::
    (Proto.Message (Output a), ToProto a) =>
    (Ptr Word8 -> Int64 -> IO ()) ->
    -- | Out pointer where the hash is written.
    Ptr Word8 ->
    -- | The optionally the hash of the block to which the message belongs, and potentially a
    --  message.
    Q.BHIQueryResponse a ->
    IO Int64
returnMessageWithBlock copier outHash response = do
    copyHashTo outHash response
    case response of
        Q.BQRNoBlock ->
            return $ queryResultCode QRNotFound
        Q.BQRBlock _ out ->
            returnMessage copier out

-- | Write the hash to the provided pointer, and if the message is given encode and
--  write it using the provided callback.
returnMaybeMessageWithBlock ::
    (Proto.Message (Output a), ToProto a) =>
    (Ptr Word8 -> Int64 -> IO ()) ->
    -- | Out pointer where the hash is written.
    Ptr Word8 ->
    -- | The optionally the hash of the block to which the message belongs, and potentially a
    --  message.
    Q.BHIQueryResponse (Maybe a) ->
    IO Int64
returnMaybeMessageWithBlock copier outHash response = do
    copyHashTo outHash response
    case response of
        Q.BQRBlock _ (Just out) ->
            returnMessage copier out
        _ ->
            return $ queryResultCode QRNotFound

-- | Write the hash to the provided pointer, and if the message is given encode and
--  write it using the provided callback.
--  If the action resulted in a @Left@ value then return 'QRInvalidArgument'.
returnEitherMessageWithBlock ::
    (Proto.Message (Output b), ToProto b) =>
    (Ptr Word8 -> Int64 -> IO ()) ->
    -- | Out pointer where the hash is written.
    Ptr Word8 ->
    -- | The optionally the hash of the block to which the message belongs, and potentially a
    --  message.
    Q.BHIQueryResponse (Either a b) ->
    IO Int64
returnEitherMessageWithBlock copier outHash response = do
    copyHashTo outHash response
    case response of
        Q.BQRBlock _ (Right out) ->
            returnMessage copier out
        Q.BQRBlock _ (Left _) -> return $ queryResultCode QRInvalidArgument
        _ ->
            return $ queryResultCode QRNotFound

-- | If the message is given encode and write it using the provided callback.
returnMessage ::
    (Proto.Message (Output a), ToProto a) =>
    (Ptr Word8 -> Int64 -> IO ()) ->
    -- | The message.
    a ->
    IO Int64
returnMessage copier = returnMessageRaw copier . toProto

-- | Encode and write the given message using the provided callback.
returnMessageRaw :: (Proto.Message msg) => (Ptr Word8 -> Int64 -> IO ()) -> msg -> IO Int64
returnMessageRaw copier v = do
    let encoded = Proto.encodeMessage v
    BS.unsafeUseAsCStringLen encoded (\(ptr, len) -> copier (castPtr ptr) (fromIntegral len))
    return $ queryResultCode QRSuccess

returnStreamWithBlock ::
    (Proto.Message (Output a), ToProto a) =>
    (Ptr Word8 -> Int64 -> IO Int32) ->
    -- | Out pointer where the hash is written.
    Ptr Word8 ->
    Q.BHIQueryResponse [a] ->
    IO Int64
returnStreamWithBlock callback outHash response = do
    copyHashTo outHash response
    case response of
        Q.BQRBlock _ messages -> do
            _ <- enqueueMessages callback messages
            return (queryResultCode QRSuccess)
        Q.BQRNoBlock -> return (queryResultCode QRNotFound)

enqueueMessages :: (Proto.Message (Output a), ToProto a) => (Ptr Word8 -> Int64 -> IO Int32) -> [a] -> IO ThreadId
enqueueMessages callback = enqueueProtoMessages callback . fmap toProto

-- | Spawn a new thread that will invoke the provided callback on the list of
--  encoded messages. If the callback response indicates that the channel to
--  which the callback is enqueueing is full, the thread will wait. The wait time
--  follows exponential backoff strategy to a maximum of 10 seconds.
enqueueProtoMessages :: (Proto.Message a) => (Ptr Word8 -> Int64 -> IO Int32) -> [a] -> IO ThreadId
enqueueProtoMessages callback = forkIO . go 0 . map Proto.encodeMessage
  where
    go _ [] = () <$ callback nullPtr maxBound -- close the sender channel.
    go n msgs@(msg : msgs') =
        BS.unsafeUseAsCStringLen msg $ \(headPtr, len) -> do
            res <- callback (castPtr headPtr) (fromIntegral len)
            if res == 0
                then go 0 msgs' -- reset wait time to 10ms.
                else
                    if res == -1
                        then do
                            let delay = (2 ^ n) * 10_000
                            threadDelay delay
                            go (min (n + 1) (10 :: Int)) msgs -- maximum delay is 10 seconds
                        else return () -- the sender channel is now dropped, so we stop.

