installer-script.script:
function installCheck() {
    if(!(system.compareVersions(system.version.ProductVersion, '10.14.0') >= 0)) {
        my.result.title = 'Unable to install';
        my.result.message = 'Concordium Node requires macOS 10.14 or later.';
        my.result.type = 'Fatal';
        return false;
    }
    if(system.files.fileExistsAtPath('/Library/Concordium Node/')) {
        my.result.title = 'Previous Installation Detected';
        my.result.message = 'A previous installation of Concordium Node exists at /Library/Concordium Node/. This installer will remove the previous installation prior to installing. Your keys and the chain data will remain untouched.';
        my.result.type = 'Warning';
        return false;
    }
    return true;
}
